        hbm_csr.hbm_port_0.cfg_hbm_eth_ctrl.write();

        data <<"pbc_csr.hbm.hbm_port_0.cfg_hbm_context.all: 0x" << pbc_csr.hbm.hbm_port_0.cfg_hbm_context.all() << endl;
data <<"pbc_csr.hbm.hbm_port_0.cfg_hbm_context.no_drop: 0x" << pbc_csr.hbm.hbm_port_0.cfg_hbm_context.no_drop() << endl;
