package dh

import (
	"math/big"
)

// Group represents a Diffie Hellman group. These groups are defined in RFCs
// 2409 and 3526.
type Group struct {
	prime     *big.Int
	generator *big.Int
}

// GroupID represents a valid Diffie Hellman group id.
type GroupID int

// Well known group IDs.
const (
	Group1  GroupID = 1
	Group2  GroupID = 2
	Group5  GroupID = 5
	Group14 GroupID = 14
	Group15 GroupID = 15
)

// Groups is a map of well known Diffie Hellman groups.
var Groups map[GroupID]Group

type dhGroupInfo struct {
	id        GroupID
	prime     string
	generator int64
}

var dhGroupInfos = []dhGroupInfo{
	{Group1, "FFFFFFFFFFFFFFFFC90FDAA22168C234C4C6628B80DC1CD129024E088A67CC74020BBEA63B139B22514A08798E3404DDEF9519B3CD3A431B302B0A6DF25F14374FE1356D6D51C245E485B576625E7EC6F44C42E9A63A3620FFFFFFFFFFFFFFFF", 2},
	{Group2, "FFFFFFFFFFFFFFFFC90FDAA22168C234C4C6628B80DC1CD129024E088A67CC74020BBEA63B139B22514A08798E3404DDEF9519B3CD3A431B302B0A6DF25F14374FE1356D6D51C245E485B576625E7EC6F44C42E9A637ED6B0BFF5CB6F406B7EDEE386BFB5A899FA5AE9F24117C4B1FE649286651ECE65381FFFFFFFFFFFFFFFF", 2},
	{Group5, "", 2},
	{Group14, "", 2},
	{Group15, "", 2},
}

func init() {
	Groups = make(map[GroupID]Group)
	for _, groupInfo := range dhGroupInfos {
		prime, _ := new(big.Int).SetString(groupInfo.prime, 16)
		Groups[groupInfo.id] = Group{
			prime:     prime,
			generator: new(big.Int).SetInt64(groupInfo.generator),
		}
	}
}

// GetGroup gets the Diffie Hellman group corresponding to the provided id.
func GetGroup(id GroupID) *Group {
	group := Groups[id]
	return &group
}
